package ru.netology.nmediaimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDatainterface MainRepository {    fun get(): LiveData<List<Post>>    fun likeBy(id: Int)    fun shareBy(id: Int)}class MainRepositoryImpl: MainRepository {    private var posts = listOf(        createPost(1),        createPost(2),        createPost(3)    )    private val data = MutableLiveData(posts)    override fun get() = data    override fun likeBy(id: Int) {        posts = posts.map {            if (it.id != id) it else it.copy(isLiked = !it.isLiked, likes = if (it.isLiked) it.likes - 1 else it.likes + 1)        }        data.value = posts    }    override fun shareBy(id: Int) {        posts = posts.map {            if (it.id != id) it else it.copy(shares = it.shares + 1)        }        data.value = posts    }    private fun createPost(id: Int) : Post {        return    Post(            id,            "Нетология. Университет интернет-профессий",            "21 мая в 18:36",            """"Привет, это новая Нетология! Когда-то Нетология начиналась с интенсивов по онлайн-маркетингу. Затем появились курсы по дизайну, разработке, аналитике и управлению. Мы растём сами и помогаем растистудентам: от новичков до уверенных профессионалов. Но самое важное остаётся с нами: мы верим, что в каждом уже есть сила, которая заставляет хотетьбольше, целиться выше, бежать быстрее. Наша миссия— помочь встать на путь роста и начать цепочкуперемен"""",            10,            990,            1132334,            false        )    }}
