package ru.netology.nmedia.mainViewimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDatainterface MainRepository {    fun get(): LiveData<List<Post>>    fun likeBy(id: Int)    fun shareBy(id: Int)    fun removeBy(id: Int)    fun save(post: Post)}class MainRepositoryImpl: MainRepository {    private var nextID = 1    private var posts = listOf(        createPostWithVieo(),        createPost(),        createPost(),        createPost()    )    private val data = MutableLiveData(posts)    override fun get() = data    override fun likeBy(id: Int) {        posts = posts.map {            if (it.id != id) it else it.copy(isLiked = !it.isLiked, likes = if (it.isLiked) it.likes - 1 else it.likes + 1)        }        data.value = posts    }    override fun shareBy(id: Int) {        posts = posts.map {            if (it.id != id) it else it.copy(shares = it.shares + 1)        }        data.value = posts    }    override fun removeBy(id: Int) {        posts = posts.filter { it.id != id }        data.value = posts    }    override fun save(post: Post) {        if (post.id == 0) {            posts = listOf(                post.copy(                    id = nextID++,                    autor = "Нетология. Университет интернет-профессий",                    date = "21 мая в 18:36"                )            ) + posts            data.value = posts            return        }        posts = posts.map {            if (it.id != post.id) it else it.copy(text = post.text)        }        data.value = posts    }    private fun createPost() : Post {        return    Post(            nextID++,            "Нетология. Университет интернет-профессий",            "21 мая в 18:36",            """"Привет, это новая Нетология! Когда-то Нетология начиналась с интенсивов по онлайн-маркетингу. Затем появились курсы по дизайну, разработке, аналитике и управлению. Мы растём сами и помогаем растистудентам: от новичков до уверенных профессионалов. Но самое важное остаётся с нами: мы верим, что в каждом уже есть сила, которая заставляет хотетьбольше, целиться выше, бежать быстрее. Наша миссия— помочь встать на путь роста и начать цепочкуперемен"""",            null,            10,            990,            1132334,            false        )    }    private fun createPostWithVieo(): Post {        return Post(            nextID++,            "Нетология. Университет интернет-профессий",            "21 мая в 18:36",            null,            "https://www.youtube.com/watch?v=7MCq8IiBp2s",            10,            990,            1132334,            false        )    }}