package ru.netology.nmedia.repositoryimport android.content.Contextimport androidx.lifecycle.LiveDataimport androidx.lifecycle.mapimport ru.netology.nmedia.dataBase.PostDaoInterfaceimport ru.netology.nmedia.dataBase.PostEntityimport ru.netology.nmedia.model.Postinterface MainRepository {    fun get(): LiveData<List<Post>>    fun likeBy(id: Int)    fun shareBy(id: Int)    fun removeBy(id: Int)    fun save(post: Post)    fun updateTextBy(id: Int, text: String)}class MainRepositoryImpl(    val postDao: PostDaoInterface): MainRepository {    override fun get() = postDao.getAll().map { posts ->        posts.map { post ->            post.mapToModel()        }    }    override fun likeBy(id: Int) = postDao.likeBy(id)    override fun shareBy(id: Int) = postDao.shareBy(id)    override fun removeBy(id: Int) = postDao.removeBy(id)    override fun save(post: Post) = postDao.insert(PostEntity.mapFromModel(post))    override fun updateTextBy(id: Int, text: String) = postDao.updateBy(id, text)}